// Initialize multi-agent orchestration
const input = $input.first();
const task = input.json.body.task;
const agents = input.json.body.agents || ['planner', 'executor', 'reviewer'];
const maxIterations = input.json.body.max_iterations || 3;
const coordinationMode = input.json.body.mode || 'sequential'; // sequential, parallel, hierarchical

const agentPrompts = {
  planner: {
    role: 'Strategic Planner',
    prompt: 'You are a strategic planning agent. Break down complex tasks into actionable steps. Identify dependencies and potential blockers. Output a structured plan with clear milestones.'
  },
  executor: {
    role: 'Task Executor',
    prompt: 'You are an execution agent. Take plans and implement them step by step. Report progress and any issues encountered. Focus on delivering concrete results.'
  },
  reviewer: {
    role: 'Quality Reviewer',
    prompt: 'You are a quality review agent. Analyze work products for correctness, completeness, and quality. Provide constructive feedback and identify areas for improvement.'
  },
  researcher: {
    role: 'Research Specialist',
    prompt: 'You are a research agent. Gather relevant information, analyze sources, and synthesize findings. Provide well-sourced and accurate research summaries.'
  },
  coder: {
    role: 'Code Developer',
    prompt: 'You are a coding agent. Write clean, efficient, and well-documented code. Follow best practices and ensure code quality.'
  },
  critic: {
    role: 'Critical Analyst',
    prompt: 'You are a critical analysis agent. Challenge assumptions, identify weaknesses, and propose alternatives. Provide balanced critique.'
  }
};

// Build agent configurations
const configuredAgents = agents.map(agentName => ({
  name: agentName,
  ...agentPrompts[agentName] || { role: agentName, prompt: `You are a ${agentName} agent.` }
}));

return {
  json: {
    task: task,
    agents: configuredAgents,
    mode: coordinationMode,
    max_iterations: maxIterations,
    current_iteration: 0,
    conversation_history: [],
    agent_outputs: {},
    status: 'initializing',
    request_id: 'orchestrate_' + Date.now()
  }
};

// Sequential agent execution
const state = $input.first().json;
const currentAgentIndex = state.conversation_history.length;

if (currentAgentIndex >= state.agents.length) {
  return {
    json: {
      ...state,
      status: 'completed',
      is_complete: true
    }
  };
}

const currentAgent = state.agents[currentAgentIndex];
const previousOutputs = state.conversation_history.map(h => 
  `[${h.agent}]: ${h.output}`
).join('\n\n');

return {
  json: {
    ...state,
    current_agent: currentAgent,
    context: previousOutputs,
    status: 'running_agent',
    is_complete: false
  }
};

// Merge agent output into state
const state = $('Prepare Sequential').first().json;
const agentResponse = $input.first().json.choices[0].message.content;

const newHistory = [...state.conversation_history, {
  agent: state.current_agent.name,
  role: state.current_agent.role,
  output: agentResponse,
  timestamp: new Date().toISOString()
}];

const newAgentOutputs = {
  ...state.agent_outputs,
  [state.current_agent.name]: agentResponse
};

return {
  json: {
    ...state,
    conversation_history: newHistory,
    agent_outputs: newAgentOutputs,
    current_iteration: state.current_iteration + 1
  }
};

// Finalize orchestration result
const state = $input.first().json;

// Generate summary from all agent outputs
const summary = Object.entries(state.agent_outputs)
  .map(([agent, output]) => `## ${agent.charAt(0).toUpperCase() + agent.slice(1)} Output\n${output}`)
  .join('\n\n---\n\n');

return {
  json: {
    success: true,
    request_id: state.request_id,
    task: state.task,
    mode: state.mode,
    agents_used: state.agents.map(a => a.name),
    iterations: state.current_iteration,
    agent_outputs: state.agent_outputs,
    conversation_history: state.conversation_history,
    combined_summary: summary,
    status: 'completed'
  }
};
